mapper.select:
SELECT D.ID, DATA_TITLE, DATA_CONTENT, DATA_PRICE, DATA_REGISTER_DATE, DATA_UPDATE_DATE, DATA_READ_COUNT, D.MEMBER_ID, MEMBER_NICKNAME, MEMBER_POINT FROM ( SELECT ROWNUM R, D.ID, DATA_TITLE, DATA_CONTENT, DATA_PRICE, DATA_REGISTER_DATE, DATA_UPDATE_DATE, DATA_READ_COUNT, D.MEMBER_ID, MEMBER_NICKNAME, MEMBER_POINT FROM ( SELECT D.ID, DATA_TITLE, DATA_CONTENT, DATA_PRICE, DATA_REGISTER_DATE, DATA_UPDATE_DATE, DATA_READ_COUNT, D.MEMBER_ID, MEMBER_NICKNAME, MEMBER_POINT FROM TBL_MEMBER M JOIN TBL_DATA D ON M.ID = D.MEMBER_ID
AND DATA_MAJOR = #{categoryType.type}
ORDER BY
DATA_READ_COUNT
ID
DESC ) D
WHERE ROWNUM <= #{dataPagination.page} * #{dataPagination.rowCount}
) D WHERE R > (#{dataPagination.page} - 1) * #{dataPagination.rowCount}

SELECT D.ID, DATA_TITLE, DATA_CONTENT, DATA_PRICE, DATA_REGISTER_DATE, DATA_UPDATE_DATE, DATA_READ_COUNT, DATA_SCHOOL, DATA_MAJOR, D.MEMBER_ID, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_SCHOOL, MEMBER_MAJOR, MEMBER_INTRODUCING, MEMBER_POINT
FROM TBL_DATA D JOIN TBL_MEMBER M
                ON M.ID = D.MEMBER_ID AND D.ID = #{id}
